mapper.select:
select lw.*,h.name as pbrname, h.accountname as pburen, h.touxiang as pbrphoto, u.name as rpname, u.touxiang as rpphoto from leaveword lw inner join huiyuan h on lw.hyid=h.id left join huiyuan u on u.id=lw.targetid where 1=1
and lw.hyid=#{hyid}
and lw.targetid=#{targetid}
${order}
and lw.id=#{id}
